using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Net;
using System.IO;

namespace PorjetoUfsmArrano.Controllers
{
    public class ToolCameras
    {
        public enum Marcas { Intelbras_VIPS3020 = 0, Intelbras_VIPBX1M = 1 };
        public static string GetSnapshot(int idCamera)
        {
            string user = "admin";
            string pass = "admin";
            string ip = "177.36.34.161";
            string porta = "1234";
            PorjetoUfsmArrano.Models.CamerasContext b = new Models.CamerasContext();
            PorjetoUfsmArrano.Models.Cameras c = b.Cameras.Find(idCamera);
            user = c.login;
            pass = c.senha;
            if (c.ip.Split(':').Length == 2)
            {
                ip = c.ip.Split(':')[0];
                porta = c.ip.Split(':')[1];
            }
            else
            {
                ip = c.ip;
                porta = "80";
            }

            try
            {

                var uri = new Uri("http://" + ip + ":" + porta + (c.id_tipo == 0 ? "/cgi-bin/snapshot.cgi" : "/snapshot.cgi"));
                switch ((int)c.id_tipo)
                {
                    case 0: uri = new Uri("http://" + ip + ":" + porta + "/cgi-bin/snapshot.cgi"); break;
                    case 1: uri = new Uri("http://" + ip + ":" + porta + "/snapshot.jpg"); break;
                }


                var cookies = new CookieContainer();
                HttpWebRequest request = (HttpWebRequest)WebRequest.Create(uri);
                request.CookieContainer = cookies;
                request.ServicePoint.Expect100Continue = false;
                request.Method = "POST";
                request.ContentType = "application/x-www-form-urlencoded";
                request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64; Trident/7.0; rv:11.0) like Gecko";
                request.Credentials = new NetworkCredential(user, pass);
                request.PreAuthenticate = true;
                var response = request.GetResponse();
                // var reader = new StreamReader(response.GetResponseStream());

                byte[] imgbytes;
                using (Stream stream = response.GetResponseStream())
                {
                    System.Drawing.Image image = System.Drawing.Image.FromStream(stream);
                    using (var ms = new MemoryStream())
                    {
                        image.Save(ms, System.Drawing.Imaging.ImageFormat.Jpeg);
                        imgbytes = ms.ToArray();
                    }
                }
                string caminho = Convert.ToBase64String(imgbytes, 0, imgbytes.Length);
                return caminho;
            }
            catch { }

            string x = "";
            int NumberChars = x.Length;
            byte[] bytes = new byte[NumberChars / 2];
            for (int i = 0; i < NumberChars; i += 2)
                bytes[i / 2] = Convert.ToByte(x.Substring(i, 2), 16);
            return Convert.ToBase64String(bytes, 0, bytes.Length);
        }
