            switch ((int)c.id_tipo)
            {
                case 0: return "http://" + user + ":" + pass + "@" + ip + ":" + porta + "/cgi-bin/mjpg/video.cgi";
                case 1: return "http://" + user + ":" + pass + "@" + ip + ":" + porta + "/channel2";

